import React from "react";
import {Link} from 'react-router-dom'

const AuthorLink = (link = '', name = 'Author' ) => {
    if(!link || !link.link)
        return false

    return (
        <div>
            <span>by </span>
            <Link to={`/author/${link.link}`} rel='author' >Author</Link>
        </div>
    )
}

export default AuthorLink