import React from "react";

const GetDate = ({ date }) => {
    const newDate = new Date(date)
    const months = [
        'January',
        'February',
        'March',
        'April',
        'May',
        'June',
        'July',
        'August',
        'September',
        'October',
        'November',
        'December'
    ]
    const formattedDate = {
        'YYYY':newDate.getFullYear(),
        'M':months[newDate.getMonth()],
        'd': newDate.getDay()
    }
    return (
        <i>
            {`${formattedDate.M} ${formattedDate.d}, ${formattedDate.YYYY}`}
        </i>
    )
}

export default GetDate