import React from "react";
import GetDate from "components/getDate"

const Post = ({ posts }) => {
    return (
        <div>
            {posts.map((post, index)=> (
                <article className='blog-post' key={index}>
                    <h2 className="blog-post-title mb-1">{post.title}</h2>
                    <p className="blog-post-meta bs-gray">
                        <GetDate date={post.dateCreated} />
                        {!!post.postedBy && <a rel="author" href="${post.postedBy}">Author</a>}
                    </p>
                    <p>{post.description}</p>
                    <hr/>
                </article>
            )) }
        </div>
    )
}

export default Post