import React from "react";
import GetDate from "components/atoms/date";
import AuthorLink from "components/atoms/authorLink";

const Post = ({ posts }) => {
  return (
    <div>
      {posts.map((post, index) => (
        <article className="blog-post" key={index}>
          <h2 className="blog-post-title mb-1">{post.title}</h2>
          <div className="blog-post-meta bs-gray">
            <GetDate date={post.dateCreated} />
            <AuthorLink link={post.postedBy} />
          </div>
          <p>{post.description}</p>
          <hr />
        </article>
      ))}
    </div>
  );
};

export default Post;
